<?phpnamespace Nemundo\Admin\Com\Button;use Nemundo\Admin\Com\Hyperlink\AdminSiteHyperlinkContainer;use Nemundo\Admin\Com\Icon\AdminIcon;use Nemundo\Admin\Site\AbstractAdminIconSite;use Nemundo\Com\Container\ContainerUserRestrictionTrait;use Nemundo\Core\Language\Translation;use Nemundo\Html\Block\Br;use Nemundo\Package\FontAwesome\IconSize;class AdminIconSiteButton extends AdminSiteHyperlinkContainer{    use ContainerUserRestrictionTrait;    /**     * @var AbstractAdminIconSite     */    public $site;    /**     * @var bool     */    public $showTitle = true;    public function getContent()    {        if ($this->checkUserVisibility()) {            $this->href = $this->site->getUrl();            $this->title = $this->site->title;            $icon = new AdminIcon($this);            $icon->icon = $this->site->iconName;            $icon->size = IconSize::X_LARGE_2;            if ($this->showTitle) {                $this->content = (new Br())->getBodyContent() . (new Translation())->getText($this->site->title);            }        }        return parent::getContent();    }}