<?phpnamespace Nemundo\Admin\Com\Button;use Nemundo\Admin\Com\Hyperlink\AdminSiteHyperlinkContainer;use Nemundo\Admin\Com\Icon\AdminIcon;use Nemundo\Admin\Site\AbstractAdminIconSite;use Nemundo\Com\Container\ContainerUserRestrictionTrait;use Nemundo\Html\Block\Br;class AdminIconSiteButton extends AdminSiteHyperlinkContainer{    use ContainerUserRestrictionTrait;    /**     * @var AbstractAdminIconSite     */    public $site;    /**     * @var bool     */    public $showTitle = true;    public function getContent()    {        //$this->site->icon->iconSize = 2;        //$this->addCssClass('p-2');        //<i class="fa-solid fa-location-dot fa-2xl" style="color: #d16915;"></i>        if ($this->checkUserVisibility()) {            //$this->addContainer($this->site->icon);            $this->href = $this->site->getUrl();            $this->title = $this->site->title;            /*if ($this->showTitle) {                $this->title = (new Translation())->getText($this->site->title);            }*/            $icon = new AdminIcon($this);            $icon->icon = $this->site->iconName;            if ($this->showTitle) {            $this->content = (new Br())->getBodyContent().$this->site->title;            }        }        return parent::getContent();    }}