<?phpnamespace Nemundo\Admin\Com\Card;use Nemundo\Admin\Com\Dropdown\AdminDropdown;use Nemundo\Html\Block\Div;use Nemundo\Html\Container\AbstractContainer;use Nemundo\Html\Container\AbstractHtmlContainer;use Nemundo\Html\Formatting\Bold;use Nemundo\Html\Inline\Span;use Nemundo\Web\Site\AbstractSite;class AdminCard extends Div{    /**     * @var string     */    public $cardTitle;    /**     * @var bool     */    //public $showDropdownMenu=false;    /**     * @var bool     */    public $showSpace=true;    // showBorderSpace    // cardTitle    /**     * @var Div     */    protected $headerDiv;    /**     * @var Span     */    protected $headerTitle;    /**     * @var AdminDropdown     */    //protected $dropdownMenu;    /**     * @var Div     */    protected $contentDiv;    protected function loadContainer()    {        $this->addCssClass('admin-card');        $this->headerDiv = new Div();        $this->headerDiv->addCssClass('admin-card-title');        $this->headerTitle=new Span($this->headerDiv);        $this->dropdownMenu=new AdminDropdown();        $this->dropdownMenu->dropdownIconName='ellipsis-vertical';        $this->contentDiv = new Div();        $this->contentDiv->addCssClass('admin-card-content');    }    /*public function addDropdownMenuSite(AbstractSite $site) {        $this->dropdownMenu->addSite($site);        return $this;    }*/    public function getContent()    {        parent::addContainer($this->headerDiv);        if ($this->cardTitle !== null) {            //parent::addContainer($this->headerDiv);            //$bold = new Bold($this->headerDiv);            //$bold->content = $this->cardTitle;            $this->headerTitle->content = $this->cardTitle;        }        /*if ($this->showDropdownMenu) {            $this->headerDiv->addContainer($this->dropdownMenu);        }*/        if ($this->showSpace) {            $this->contentDiv->addCssClass('admin-card-content-text');        }        parent::addContainer($this->contentDiv);        return parent::getContent();    }    public function addContainer(AbstractContainer $container)    {        $this->contentDiv->addContainer($container);    }}