<?phpnamespace Nemundo\Admin\Com\Card;use Nemundo\Html\Block\Div;use Nemundo\Html\Container\AbstractContainer;use Nemundo\Html\Heading\H5;class Admin2Card extends Div{    /**     * @var string     */    public $cardTitle;    /**     * @var Div     */    protected $headerDiv;    /**     * @var Div     */    protected $contentDiv;    protected function loadContainer()    {        $this->addCssClass('nemundo-card');        /*$this->addCssClass('mb-3');*/        $this->headerDiv = new Div();        $this->headerDiv->addCssClass('nemundo-card-header');        parent::addContainer($this->headerDiv);        $this->contentDiv = new Div();        $this->contentDiv->addCssClass('nemundo-card-body');        parent::addContainer($this->contentDiv);    }    public function getContent()    {        if ($this->cardTitle !== null) {            $h5 = new H5($this->headerDiv);            $h5->content = $this->cardTitle;        }        /*if ($this->title !== null) {            $bold = new Bold($this->headerDiv);            $bold->content = $this->title;        }*/        return parent::getContent();    }    public function addContainer(AbstractContainer $container)    {        $this->contentDiv->addContainer($container);    }}