<?phpnamespace Nemundo\Admin\Com\ListBox;use Nemundo\Admin\Com\Image\AdminImage;use Nemundo\Com\FormBuilder\Item\AbstractFileUpload;use Nemundo\Html\Form\Formatting\Label;class AdminFileUpload extends AbstractFileUpload{    use AdminErrorMessageTrait;    public function getContent()    {        $this->prepareHtml();        $this->tagName = 'div';        $this->addCssClass('admin-textbox');        $label = new Label();        $label->for = $this->name;        $label->content = $this->getLabelErrorMessage();        $this->fileInput->addCssClass('admin-file-input');        $this->addContainer($label);        $this->addContainer($this->fileInput);        // AdminImageUpload        if ($this->value!==null) {            $image = new AdminImage($this);            $image->src = $this->value;        }        return parent::getContent();    }}