<?phpnamespace Nemundo\Admin\Com\ListBox;use Nemundo\Com\FormBuilder\Item\AbstractLargeTextBox;use Nemundo\Html\Formatting\Bold;use Nemundo\Html\Form\Formatting\Label;use Nemundo\Core\Language\Translation;use Nemundo\Package\Bootstrap\Utility\BootstrapSpacing;// LargeTextBoxclass AdminLargeTextBox extends AbstractLargeTextBox{    use AdminErrorMessageTrait;    protected function loadContainer()    {        parent::loadContainer();        $this->rows = 10;    }    public function getContent()    {        $this->prepareHtml();        $this->tagName = 'div';        $this->addCssClass('admin-textbox');        $label = new Label();        $label->content = $this->getLabelErrorMessage();        $this->textarea->addCssClass('admin-textarea');        $this->addContainer($label);        $this->addContainer($this->textarea);        return parent::getContent();    }}