<?phpnamespace Nemundo\Admin\Com\Pagination;use Nemundo\Admin\Parameter\PageParameter;use Nemundo\Com\Pagination\PaginationTrait;use Nemundo\Core\Structure\ForLoop;use Nemundo\Html\Block\Div;use Nemundo\Html\Hyperlink\Hyperlink;use Nemundo\Web\Site\Site;use Nemundo\Web\Url\UrlParameterBuilder;class AdminPagination extends Div{    use PaginationTrait;    public function getContent()    {        $this->addCssClass('admin-pagination');        $this->loadPageRequest();        $currentPage = (new PageParameter())->getValue();        $this->totalCount = $this->paginationReader->getTotalCount();        if ($this->totalCount <= $this->paginationLimit) {            $this->visible = false;        }        if ($this->showPreviousNext) {            // Previous            $hyperlink = new Hyperlink($this);            $hyperlink->addCssClass('admin-pagination-link');            $hyperlink->content = 'ZurÃ¼ck';            //$hyperlink->href = (new Site())->addParameter(new PageParameter($currentPage - 1))->getUrl();            if ($this->getCurrentPage() > 0) {                $hyperlink->href = (new Site())->addParameter(new PageParameter($currentPage - 1))->getUrl();            } else {                //$li->addCssClass('disabled');            }        }        if ($this->showNumber) {            $loop = new ForLoop();            $loop->minNumber = $this->getPaginationFrom();            $loop->maxNumber = $this->getPaginationTo();            foreach ($loop->getData() as $number) {                $hyperlink = new Hyperlink($this);                $hyperlink->content = $number + 1;                $parameter = new PageParameter($number);                $url = new UrlParameterBuilder();                $url->addParameter($parameter);                $hyperlink->href = $url->getUrl();                $hyperlink->addCssClass('admin-pagination-item');                if ($this->getCurrentPage() == $number) {                    $hyperlink->addCssClass('admin-pagination-active');                } else {                    $hyperlink->addCssClass('admin-pagination-link');                }            }        }        if ($this->showPreviousNext) {            $hyperlink = new Hyperlink($this);            $hyperlink->addCssClass('admin-pagination-link');            $hyperlink->content = 'Weiter';            $hyperlink->href = (new Site())->addParameter(new PageParameter($currentPage + 1))->getUrl();            if ((($this->getCurrentPage() + 1) * $this->paginationLimit) > $this->totalCount) {                //$li->addCssClass('disabled');            }        }        return parent::getContent();    }}