<?phpnamespace Nemundo\Admin\Com\Table\Sorting;use Nemundo\Admin\Com\Table\Header\UpDownSortingHyperlink;class AdminUpdDownSortingHyperlink extends UpDownSortingHyperlink{    /**     * @var bool     */    public $alignmentTextRight = false;    public function getContent()    {        $this->hyperlink->addCssClass('text-decoration-none');        $this->hyperlink->addCssClass('text-muted');        if ($this->alignmentTextRight) {            $this->addCssClass('admin-text-right');        } else {            $this->addCssClass('admin-text-left');        }        return parent::getContent();    }}