<?phpnamespace Nemundo\Admin\Com\Widget;use Nemundo\Html\Block\Div;use Nemundo\Html\Container\AbstractContainer;use Nemundo\Html\Inline\Span;abstract class AbstractAdminWidget extends Div{    /**     * @var string     */    protected $widgetTitle;    /**     * @var bool     */    public $showSpace = true;    /**     * @var Div     */    protected $headerDiv;    /**     * @var Span     */    protected $headerTitle;    /**     * @var Div     */    protected $contentDiv;    protected function loadContainer()    {        $this->addCssClass('admin-card');        $this->headerDiv = new Div();        $this->headerDiv->addCssClass('admin-card-title');        $this->headerTitle = new Span($this->headerDiv);        $this->contentDiv = new Div();        $this->contentDiv->addCssClass('admin-card-content');    }    public function getContent()    {        parent::addContainer($this->headerDiv);        if ($this->widgetTitle !== null) {            $this->headerTitle->content = $this->widgetTitle;        } else {            $this->headerDiv->visible = false;        }        if ($this->showSpace) {            $this->contentDiv->addCssClass('admin-card-content-text');        }        parent::addContainer($this->contentDiv);        return parent::getContent();    }    public function addContainer(AbstractContainer $container)    {        $this->contentDiv->addContainer($container);    }}