<?phpnamespace Nemundo\Admin\Site;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Template\NavbarAdminTemplate;use Nemundo\Admin\UniqueId\Widget\UniqueIdAdminWidget;use Nemundo\App\UserAction\Com\Card\LoginCard;use Nemundo\Web\Site\AbstractSite;class AdminHomeSite extends AbstractSite{    protected function loadSite()    {        $this->title = 'Home';        $this->url = '';    }    public function loadContent()    {        $page = new NavbarAdminTemplate();        $layout = new AdminFlexboxLayout($page);        $card = new LoginCard($layout);        $card->showForgotHyperlink = true;        new UniqueIdAdminWidget($layout);        $page->render();    }}