<?phpnamespace Nemundo\Admin\Site;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Template\NavbarAdminTemplate;use Nemundo\Admin\UniqueId\Widget\UniqueIdAdminWidget;use Nemundo\App\UserAction\Widget\LoginWidget;use Nemundo\Html\Container\HtmlContainer;use Nemundo\Web\Site\AbstractSite;class AdminHomeSite extends AbstractSite{    protected function loadSite()    {        $this->title = 'Home';        $this->url = '';    }    public function loadContent()    {        $page = new NavbarAdminTemplate();        $layout = new AdminFlexboxLayout($page);        new LoginWidget($layout);        new UniqueIdAdminWidget($layout);        $page->render();    }}