<?phpnamespace Nemundo\App\Apache\Cmd;use Nemundo\App\Apache\Builder\ApacheConfigBuilder;use Nemundo\App\Linux\Cmd\AbstractCmd;use Nemundo\App\Linux\Cmd\ChownCmd;use Nemundo\App\Linux\Cmd\MkdirCmd;use Nemundo\App\Linux\Cmd\TextFileCreatorCmd;class ApacheConfigCreatorCmd extends AbstractCmd{    public $configPath = '/etc/apache2/sites-enabled';    public $serverName;    public $path;    public function getCommandList()    {        $builder = new ApacheConfigBuilder();        $builder->configPath = $this->configPath;        $builder->serverName = $this->serverName;        $builder->path = $this->path;        $content = $builder->getContent();        $cmd = new TextFileCreatorCmd();        $cmd->filename = '/etc/apache2/sites-enabled/' . $this->serverName . '.conf';        $cmd->content = $content;        $this->addCommand($cmd);        $cmd = new MkdirCmd();        $cmd->path = $this->path;        $this->addCommand($cmd);        $cmd = new ChownCmd();        $cmd->user = 'www-data';        $cmd->path = $this->path;        $cmd->recursive=false;        $this->addCommand($cmd);        $cmd = new ApacheRestartCmd();        $this->addCommand($cmd);        return parent::getCommandList();    }}