<?phpnamespace Nemundo\App\Apache\Com\Form;use Nemundo\App\Apache\Builder\ApacheConfigBuilder;use Nemundo\Package\Bootstrap\Form\BootstrapForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;class ApacheConfigForm extends BootstrapForm{    /**     * @var BootstrapTextBox     */    public $serverName;    /**     * @var BootstrapTextBox     */    public $path;    public function getContent()    {        $this->serverName = new BootstrapTextBox($this);        $this->serverName->label = 'Server Name';        $this->serverName->validation = true;        $this->path = new BootstrapTextBox($this);        $this->path->label = 'Path';        $this->path->validation = true;        return parent::getContent();    }    protected function onSubmit()    {        $builder = new ApacheConfigBuilder();        $builder->serverName = $this->serverName->getValue();        $builder->path = $this->path->getValue();        $builder->createConfigFile();    }}