<?phpnamespace Nemundo\App\Apache\Page;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminClickableTableRow;use Nemundo\App\Apache\Com\Form\ApacheConfigForm;use Nemundo\App\Apache\Parameter\FilenameParameter;use Nemundo\App\Apache\Reader\ApacheConfigFileReader;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Core\File\DirectoryReader;use Nemundo\Core\Path\Path;use Nemundo\Core\TextFile\Reader\TextFileReader;use Nemundo\Core\Type\Text\Html;use Nemundo\Html\Typography\Code;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Web\Site\Site;class ApachePage extends AbstractTemplateDocument{    public function getContent()    {        new ApacheConfigForm($this);        $path = '/etc/apache2/sites-enabled';        $layout = new BootstrapTwoColumnLayout($this);        $table = new AdminClickableTable($layout->col1);        $header = new AdminTableHeader($table);        $header->addText('Config');        /*$reader = new DirectoryReader();        $reader->path = $path;        $reader->includeFiles = true;        $reader->includeDirectories = false;*/        $reader=new ApacheConfigFileReader();        foreach ($reader->getData() as $file) {            $row = new AdminClickableTableRow($table);            $row->addText($file->filename);            $row->addText($file->fullFilename);            $site = new Site();            $site->addParameter(new FilenameParameter($file->filename));            $row->addClickableSite($site);        }        $parameter = new FilenameParameter();        if ($parameter->hasValue()) {            $filename = (new Path($path))                ->addPath($parameter->getValue())                ->getFullFilename();            $code = new Code($layout->col2);            $code->content = (new Html( (new TextFileReader($filename))->getText()))->getValue();        }        return parent::getContent();    }}