<?phpnamespace Nemundo\App\Apache\Reader;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminClickableTableRow;use Nemundo\App\Apache\Parameter\FilenameParameter;use Nemundo\Core\Base\DataSource\AbstractDataSource;use Nemundo\Core\File\DirectoryReader;use Nemundo\Core\File\File;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Web\Site\Site;class ApacheConfigFileReader extends AbstractDataSource{    public $path =  '/etc/apache2/sites-enabled';    /**     * @return File[]     */    public function getData()    {        return parent::getData(); // TODO: Change the autogenerated stub    }    protected function loadData()    {        $reader = new DirectoryReader();        $reader->path = $this->path;        $reader->includeFiles = true;        $reader->includeDirectories = false;        foreach ($reader->getData() as $file) {            $this->addItem($file);        }    }}