<?phpnamespace Nemundo\App\Application\Page;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\App\Application\Template\ApplicationTemplate;use Nemundo\Core\File\DirectoryReader;use Nemundo\Core\Path\PathSize;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Project\Path\CachePath;use Nemundo\Project\Path\TmpPath;class CachePage extends ApplicationTemplate{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $p = new Paragraph($layout);        $p->content = 'Cache Path: ' . (new CachePath())->getPath();        if ((new CachePath())->existPath()) {            $p = new Paragraph($layout);            $p->content = 'Cache Path Size: ' . (new PathSize((new TmpPath())->getPath()))->getFileSize()->getText();            $table = new AdminTable($layout);            $header = new AdminTableHeader($table);            $header->addText('Filename');            $reader = new DirectoryReader();            $reader->path = (new TmpPath())->getPath();            foreach ($reader->getData() as $file) {                $row = new AdminTableRow($table);                $row->addText($file->getFilename());            }        }        return parent::getContent();    }}