<?phpnamespace Nemundo\App\Application\Page;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\App\Application\Com\Form\TmpUploadForm;use Nemundo\App\Application\Template\ApplicationTemplate;use Nemundo\Core\File\DirectoryReader;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Project\Path\TmpPath;class TmpPage extends ApplicationTemplate{    public function getContent()    {        $layout=new BootstrapTwoColumnLayout($this);        $table = new AdminTable($layout->col1);        $reader = new DirectoryReader();        $reader->path = (new TmpPath())->getPath();        foreach ($reader->getData() as $file) {            $row=new AdminTableRow($table);            $row->addText($file->getFilename());        }        new TmpUploadForm($layout->col2);        return parent::getContent();    }}