<?phpnamespace Nemundo\App\Application\Reset;use Nemundo\App\Application\Data\Application\ApplicationDelete;use Nemundo\App\Application\Data\Application\ApplicationUpdate;use Nemundo\App\Application\Data\Project\ProjectDelete;use Nemundo\App\Application\Data\Project\ProjectUpdate;use Nemundo\Project\Reset\AbstractReset;class ApplicationReset extends AbstractReset{    public function reset()    {        $update = new ApplicationUpdate();        $update->setupStatus = false;        $update->update();        $update = new ProjectUpdate();        $update->setupStatus = false;        $update->update();    }    public function remove()    {        $delete = new ApplicationDelete();        $delete->filter->andEqual($delete->model->setupStatus, false);        $delete->delete();        $delete = new ProjectDelete();        $delete->filter->andEqual($delete->model->setupStatus, false);        $delete->delete();    }}