<?phpnamespace Nemundo\App\Application\Site;use Nemundo\App\Application\Page\CachePage;use Nemundo\App\Application\Page\TmpPage;use Nemundo\Web\Site\AbstractSite;class CacheSite extends AbstractSite{    /**     * @var TmpSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Cache';        $this->url = 'cache';        CacheSite::$site = $this;    }    public function loadContent()    {        (new CachePage())->render();    }}