<?phpnamespace Nemundo\App\Application\Site;use Nemundo\Admin\Site\AbstractClearSite;use Nemundo\App\Application\Parameter\ProjectParameter;use Nemundo\Core\Http\Url\UrlRedirect;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\Web\Url\UrlParameterBuilder;class ClearSite extends AbstractClearSite{    /**     * @var ClearSite     */    public static $site;    protected function loadSite()    {        ClearSite::$site = $this;    }    public function loadContent()    {        $urlBuilder = (new UrlParameterBuilder((new UrlReferer())->getUrl()))->removeParameter(new ProjectParameter());        (new UrlRedirect())->redirect($urlBuilder->getUrl());    }}