<?phpnamespace Nemundo\App\Application\Site;use Nemundo\App\Application\Page\TmpPage;use Nemundo\Web\Site\AbstractSite;class TmpSite extends AbstractSite{    /**     * @var TmpSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Tmp';        $this->url = 'tmp';        TmpSite::$site = $this;    }    public function loadContent()    {        (new TmpPage())->render();    }}