<?phpnamespace Nemundo\App\Application\Site;use Nemundo\App\Application\Page\TmpPage;use Nemundo\App\Application\Page\WebTmpPage;use Nemundo\Web\Site\AbstractSite;class WebTmpSite extends AbstractSite{    /**     * @var TmpSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Web Tmp';        $this->url = 'web-tmp';        WebTmpSite::$site = $this;    }    public function loadContent()    {        (new WebTmpPage())->render();    }}