<?phpnamespace Nemundo\App\Backup\Scheduler;use Nemundo\App\Backup\Data\Backup\BackupReader;use Nemundo\App\Backup\Path\BackupPath;use Nemundo\App\Backup\Path\DumpBackupPath;use Nemundo\App\Backup\Type\AbstractBackup;use Nemundo\App\Scheduler\Job\AbstractScheduler;use Nemundo\Core\Archive\ZipArchive;use Nemundo\Core\File\File;use Nemundo\Core\Http\Domain\DomainInformation;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Core\Type\Text\Text;use Nemundo\Db\Provider\MySql\Dump\MySqlDump;class BackupDumpScheduler extends AbstractScheduler{    protected function loadScheduler()    {        $this->active = false;        $this->scriptName = 'backup-dump';        $this->overrideSetting = false;        $this->description = 'Create a MySql Dump File in path /backup/dump';        $this->consoleScript = true;    }    public function run()    {        $reader = new BackupReader();        foreach ($reader->getData() as $backupRow) {            $className = $backupRow->phpClass;            /** @var AbstractBackup $backup */            $backup = new $className;            $backup->export();        }    }}