<?phpnamespace Nemundo\App\Backup\Scheduler;use Nemundo\App\Backup\Data\Backup\BackupReader;use Nemundo\App\Backup\Type\AbstractBackup;use Nemundo\App\Scheduler\Job\AbstractScheduler;class BackupDumpScheduler extends AbstractScheduler{    protected function loadScheduler()    {        $this->active = false;        $this->scriptName = 'backup-dump';        $this->overrideSetting = false;        $this->description = 'Create a MySql Dump File in path /backup/dump';        $this->consoleScript = true;    }    public function run()    {        $reader = new BackupReader();        foreach ($reader->getData() as $backupRow) {            $className = $backupRow->phpClass;            /** @var AbstractBackup $backup */            $backup = new $className;            $backup->export();        }    }}