<?phpnamespace Nemundo\App\Backup\Site;use Nemundo\App\Backup\Page\BackupPage;use Nemundo\Web\Site\AbstractSite;class BackupSite extends AbstractSite{    /**     * @var BackupSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Backup';        $this->url = 'backup';        BackupSite::$site = $this;        new ExportSite($this);        new ImportSite($this);        new DownloadSite($this);        new ZipDownloadSite($this);        new UploadSite($this);    }    public function loadContent()    {        (new BackupPage())->render();    }}