<?phpnamespace Nemundo\App\Backup\Site;use Nemundo\App\Backup\Path\ImportBackupPath;use Nemundo\Core\Archive\ZipExtractor;use Nemundo\Core\File\File;use Nemundo\Package\Dropzone\DropzoneFileRequest;use Nemundo\Web\Site\AbstractSite;class UploadSite extends AbstractSite{    /**     * @var UploadSite     */    public static $site;    protected function loadSite()    {        $this->url = 'upload';        UploadSite::$site = $this;    }    public function loadContent()    {        (new ImportBackupPath())->createPath();        $fileRequest = new DropzoneFileRequest();        $filename = $fileRequest->saveAsOrginalFilename((new ImportBackupPath())->getPath());        $zip = new ZipExtractor();        $zip->archiveFilename = $filename;        $zip->extractPath = (new ImportBackupPath())->getPath();        $zip->extract();        (new File($filename))->deleteFile();    }}