<?phpnamespace Nemundo\App\Backup\Site;use Nemundo\App\Backup\Parameter\FileParameter;use Nemundo\App\Backup\Path\ExportBackupPath;use Nemundo\Core\Archive\ZipArchive;use Nemundo\Core\File\DirectoryReader;use Nemundo\Core\Http\Response\FileResponse;use Nemundo\Project\Path\TmpPath;use Nemundo\Web\Site\AbstractSite;class ZipDownloadSite extends AbstractSite{    /**     * @var DownloadSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Zip Download';        $this->url = 'zip-download';        ZipDownloadSite::$site = $this;    }    public function loadContent()    {        $filename = (new TmpPath())            ->addPath('backup.zip')            ->getFullFilename();        $zip = new ZipArchive();        $zip->archiveFilename = $filename;        $reader = new DirectoryReader();        $reader->path = (new ExportBackupPath())->getPath();        foreach ($reader->getData() as $file) {            $zip->addFilename($file->getFilename());        }        $zip->createArchive();        $response = new FileResponse();        $response->filename = $filename;        $response->sendResponse();    }}