<?phpnamespace Nemundo\App\Composer\Page;use Nemundo\Admin\Com\Copy\AdminCopyTextBox;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Local\LocalCommand;use Nemundo\Core\Type\Text\Text;use Nemundo\Project\ProjectConfig;class ComposerPage extends AbstractTemplateDocument{    public function getContent()    {        $path = ProjectConfig::$projectPath;        //$path = 'C:\git\lib\nemundo_com';        $line = 'cd ' . $path . ' && composer show';        //(new Debug())->write($line);        $copy = new AdminCopyTextBox($this);        $copy->value=$line;        $table = new AdminBootstrapTable($this);        $cmd = new LocalCommand();        $cmd->addCommand($line);        $value = $cmd->runCommand();   //runLocalCommand($line);        foreach ($value as $version) {            $row = new AdminTableRow($table);            $row->addText($version);            $list= (new Text($version))->split(chr(9));            foreach ($list as $value) {                $row->addText($value);            }        }        return parent::getContent();    }}