<?phpnamespace Nemundo\App\CssDesigner\Builder;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\TextFile\Writer\TextFileWriter;class StyleBuilder extends AbstractBase{    public $filename;    public $lightColor;    public $darkColor;    public $backgroundColor;    public $fontColor;    public $borderRadius;    public $importFrameworkCss = '../framework/framework.css';    public function createStylesheet()    {        $writer = new TextFileWriter($this->filename);        $writer->overwriteExistingFile = true;        $writer            //->addLine('@import "../framework/framework.css";')            ->addLine('@import "' . $this->importFrameworkCss . '";')            ->addLine(':root {')            ->addLine('--color-light: ' . $this->lightColor . ';')            ->addLine('--color-dark: ' . $this->darkColor . ';')            ->addLine('--color-background: ' . $this->backgroundColor . ';')            ->addLine('--color-font: ' . $this->fontColor . ';')            ->addLine('')            ->addLine('--box-shadow: 3px 6px 4px -6px #000000;')            ->addLine('--border-radius: ' . $this->borderRadius . 'px;')            ->addLine('}')            ->addLine('')            ->writeFile();    }}