<?phpnamespace Nemundo\App\FileLog\Form;use Nemundo\Admin\Com\Form\AdminSearchForm;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\Core\File\DirectoryReader;use Nemundo\Core\Log\LogConfig;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class FileLogForm extends AdminSearchForm{    /**     * @var AdminListBox     */    private $listbox;    protected function loadContainer()    {        parent::loadContainer();        $this->listbox = new AdminListBox($this);        $this->listbox->name = 'file';    }    public function getLogFilename()    {        $filename = $this->listbox->getValue();        return $filename;    }    public function getContent()    {        $this->listbox->label = 'Log File';        $this->listbox->submitOnChange = true;        $this->listbox->value = $this->listbox->getValue();        $reader = new DirectoryReader();        $reader->path = LogConfig::$logPath;        foreach ($reader->getData() as $file) {            $this->listbox->addItem($file->getFilename(), $file->getFilename());        }        return parent::getContent();    }}