<?phpnamespace Nemundo\App\FileLog\Page;use Nemundo\Admin\Com\Button\AdminSiteButton;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\App\FileLog\Form\FileLogForm;use Nemundo\App\FileLog\Parameter\FilenameParameter;use Nemundo\App\FileLog\Site\FileLogDeleteSite;use Nemundo\App\FileLog\Site\FileLogDownloadSite;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Core\Type\Number\Number;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Project\Path\LogPath;class FileLogPage extends AbstractTemplateDocument{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $btn = new AdminSiteButton($layout);        $btn->site = FileLogDeleteSite::$site;        $form = new FileLogForm($layout);        $filename = $form->getLogFilename();        if ($filename !== '') {            $btn = new AdminSiteButton($layout);            $btn->site = FileLogDownloadSite::$site;            $btn->site->addParameter(new FilenameParameter($filename));            $p = new Paragraph($layout);            $table = new AdminTable($layout);            $fullFilename = (new LogPath())                ->addPath($filename)                ->getFullFilename();            $f = fopen($fullFilename, 'r');            $lineCount = 0;            $startLine = 0;            $endLine = 1000;            while ($line = fgets($f)) {                $lineCount++;                $showLine = false;                if (($lineCount >= $startLine) && ($lineCount <= $endLine)) {                    $showLine = true;                }                if ($showLine) {                    $row = new TableRow($table);                    $row->addText($line);                }            }            fclose($f);            $p->content = (new Number($lineCount))->getFormatNumber() . ' lines';        }        return parent::getContent();    }}