<?phpnamespace Nemundo\App\FileLog\Site;use Nemundo\App\FileLog\Page\FileLogPage;use Nemundo\Web\Site\AbstractSite;class FileLogSite extends AbstractSite{    /**     * @var FileLogSite     */    public static $site;    protected function loadSite()    {        $this->title = 'File Log';        $this->url = 'file-log';        FileLogSite::$site = $this;        new FileLogDownloadSite($this);        new FileLogDeleteSite($this);    }    public function loadContent()    {        (new FileLogPage())->render();    }}