<?phpnamespace Nemundo\App\Git\Command;use Nemundo\App\Linux\Cmd\AbstractCmd;use Nemundo\Core\Command\AbstractCommand;use Nemundo\Core\Command\AbstractLocalCommand;class GitCloneCommand extends AbstractGitCommand{    public $gitUrl;    //public $path;    public function getCommandList()    {        $this->clearCommand();        $this->addCommandLine('git clone ' . $this->gitUrl . ' ' . $this->path);        return parent::getCommandList();    }}