<?phpnamespace Nemundo\App\Linux\Cmd;use Nemundo\Core\Local\AbstractLocalCommand;class ChownCmd extends AbstractCmd{    public $path;    public $user;    /**     * @var bool     */    public $recursive = true;    public function getCommandList()    {        $line = 'chown ';        if ($this->recursive) {            $line.= ' -R ';        }        $line.= $this->user . ':' . $this->user . ' ' . $this->path;        $this->addCommandLine($line);        //$this->addCommandLine('chown -R ' . $this->user . ':' . $this->user . ' ' . $this->path);        return parent::getCommandList(); // TODO: Change the autogenerated stub    }   /* public function getCommand()    {        $this->changeOwner();        //parent::getCommand(); // TODO: Change the autogenerated stub    }*/}