<?phpnamespace Nemundo\App\Linux\Cmd;class TextFileCreatorCmd extends AbstractCmd{    public $filename;    public $content;    protected function loadCommand()    {        // TODO: Implement loadCmd() method.    }    public function createFile() {       $this->addCommandLine('echo "'.$this->content.'" | sudo tee '.$this->filename.';');    }    public function getCommandList()    {        $this->createFile();        return parent::getCommandList(); // TODO: Change the autogenerated stub    }}