<?phpnamespace Nemundo\App\Mail\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\App\Mail\Message\Mail\MailMessage;use Nemundo\Core\Validation\ValidationType;class TestMailForm extends AbstractAdminForm{    /**     * @var AdminTextBox     */    private $mailTo;    /**     * @var AdminTextBox     */    private $subject;    /**     * @var AdminLargeTextBox     */    private $text;    public function getContent()    {        $this->mailTo = new AdminTextBox($this);        $this->mailTo->label = 'To';        $this->mailTo->validation = true;        $this->mailTo->validationType = ValidationType::IS_EMAIL;        $this->mailTo->autofocus = true;        $this->subject = new AdminTextBox($this);        $this->subject->label = 'Subject';        $this->subject->validation = true;        $this->subject->value = 'Test Mail';        $this->text = new AdminLargeTextBox($this);        $this->text->label = 'Text';        $this->text->value = 'Test Mail';        $this->submitButton->label = 'Send Mail';        return parent::getContent();    }    protected function onSubmit()    {        $message = new MailMessage();        $message->mailTo = $this->mailTo->getValue();        $message->subject = $this->subject->getValue();        $message->text = $this->text->getValue();        $message->sendMail();    }}