<?phpnamespace Nemundo\App\Mail;use Nemundo\App\Mail\Com\Document\AbstractActionMailHtmlDocument;use Nemundo\App\Mail\Com\Document\ActionMailHtmlDocument;use Nemundo\App\Mail\Connection\SmtpConnection;class MailConfig{    /**     * @var SmtpConnection     */    public static $mailConnection;    /**     * @var string     */    public static $defaultMailFrom;    /**     * @var string     */    public static $defaultMailFromText;    /**     * @var string     */    public static $actionMailDocumentClass;    public function getActionMailDocument()    {        /** @var AbstractActionMailHtmlDocument $document */        $document = null;        if (MailConfig::$actionMailDocumentClass !== null) {            /** @var AbstractActionMailHtmlDocument $document */            $document = new MailConfig::$actionMailDocumentClass();        } else {            $document = new ActionMailHtmlDocument();        }        return $document;    }}