<?phpnamespace Nemundo\App\Mail\Message;use Nemundo\App\Mail\Data\MailQueue\MailQueue;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Log\LogMessage;use Nemundo\Core\Validation\EmailValidation;// TextMailMailMessageabstract class AbstractMailMessage extends AbstractBase{    /**     * @var string     */    protected $mailTo;    /**     * @var string     */    protected $subject;    /**     * @var string     */    protected $text;    public function __construct() {        $this->loadMailMessage();    }    protected function loadMailMessage() {    }    public function sendMail()    {        if ((new EmailValidation())->isEmail($this->mailTo)) {            $data = new MailQueue();            $data->mailTo = $this->mailTo;            $data->subject = $this->subject;            $data->text = $this->text;            $data->send = false;            $data->save();        } else {            (new LogMessage())->writeError('MailMessage: No valid eMail ' . $this->mailTo);            //return;        }    }}