<?phpnamespace Nemundo\App\Mail\Message\Base;use Nemundo\App\Mail\Message\Attachment\AbstractInlineImageAttachment;use Nemundo\App\Mail\Message\Attachment\InlineImageAttachment;use Nemundo\Core\Base\AbstractBase;abstract class AbstractMessage extends AbstractBase{    /**     * @var string     */    protected $mailTo;    /**     * @var string     */    protected $subject;    /**     * @var string     */    protected $text;    /**     * @var InlineImageAttachment[]     */    protected $inlineImageList = [];    protected $attachmentList = [];    abstract public function sendMail();    public function __construct()    {        $this->loadMailMessage();    }    protected function loadMailMessage()    {    }    public function addInlineImage(AbstractInlineImageAttachment $inlineImage)    {        if ($inlineImage !== null) {            $this->inlineImageList[] = $inlineImage;        }        return $this;    }    public function addAttachment($filename)    {        $this->attachmentList[] = $filename;        return $this;    }}