<?phpnamespace Nemundo\App\Mail\Script;use Nemundo\App\Mail\Data\Attachment\AttachmentDelete;use Nemundo\App\Mail\Data\InlineImage\InlineImageDelete;use Nemundo\App\Mail\Data\MailQueue\MailQueueDelete;use Nemundo\App\Script\Type\AbstractScript;class MailQueueDeleteScript extends AbstractScript{    protected function loadScript()    {        $this->scriptName = 'mailqueue-delete';        $this->consoleScript = true;        $this->description = 'Delete the mail queue';    }    public function run()    {        (new InlineImageDelete())->delete();        (new AttachmentDelete())->delete();        (new MailQueueDelete())->delete();    }}