<?phpnamespace Nemundo\App\Mail\Site;use Nemundo\Admin\Site\AbstractDeleteIconSite;use Nemundo\App\Mail\Data\MailQueue\MailQueueDelete;use Nemundo\App\Mail\Parameter\MailParameter;use Nemundo\Core\Http\Url\UrlReferer;class MailDeleteQueueSite extends AbstractDeleteIconSite{    /**     * @var MailDeleteQueueSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Delete Mail';        $this->url='delete-mail';        MailDeleteQueueSite::$site = $this;    }    public function loadContent()    {        (new MailQueueDelete())->deleteById((new MailParameter())->getValue());        (new UrlReferer())->removeAllParameter()->redirect();    }}