<?phpnamespace Nemundo\App\Mail\Site;use Nemundo\App\Mail\Page\MailPage;use Nemundo\Web\Site\AbstractSite;class MailSite extends AbstractSite{    /**     * @var MailSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Mail';        $this->url = 'mail';        MailSite::$site = $this;        new MailViewSite($this);        new MailDeleteQueueSite($this);        new TestMailSite($this);        new ConfigSite($this);    }    public function loadContent()    {        (new MailPage())->render();    }}