<?phpnamespace Nemundo\App\ModelDesigner\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\App\ModelDesigner\Com\ListBox\IndexListBox;use Nemundo\App\ModelDesigner\Parameter\AppParameter;use Nemundo\App\ModelDesigner\Parameter\ModelParameter;use Nemundo\App\ModelDesigner\Parameter\ProjectParameter;class IndexForm extends AbstractAdminForm{    /**     * @var AdminTextBox     */    private $indexName;    /**     * @var IndexListBox     */    private $indexType;    public function getContent()    {        $this->indexName = new AdminTextBox($this);        $this->indexName->label = 'Index Name';        $this->indexName->validation = true;        $this->indexType = new IndexListBox($this);        $this->indexType->validation = true;        return parent::getContent();    }    protected function onSubmit()    {        $project = (new ProjectParameter())->getProject();        $app = (new AppParameter())->getApp($project);        $model = (new ModelParameter())->getModel($app);        $index = $this->indexType->getIndex();        $index->indexName = $this->indexName->getValue();        $model->addIndex($index);        $app->writeJson();    }}