<?phpnamespace Nemundo\App\ModelDesigner\Com\ListBox;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\App\ModelDesigner\Json\ProjectJson;use Nemundo\App\ModelDesigner\ModelDesignerConfig;class ModelListBox extends AdminListBox{    public function getContent()    {        $this->label = 'Model';        $list = [];        $projectCollection = (new ModelDesignerConfig())->getProjectCollection();        foreach ($projectCollection->getProjectList() as $project) {            $projectJson = new ProjectJson($project);            foreach ($projectJson->getAppJsonList() as $appJson) {                foreach ($appJson->getModelList() as $model) {                    $list[$model->modelId] = $model->className . ' (' . $appJson->appLabel . ')';                }            }        }        asort($list);        foreach ($list as $key => $value) {            $this->addItem($key, $value);        }        return parent::getContent();    }}