<?phpnamespace Nemundo\App\ModelDesigner\Com\ListBox;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\App\ModelDesigner\Collection\ModelTemplateCollection;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class ModelTemplateListBox extends AdminListBox  // BootstrapListBox{    public function getContent()    {        $this->label = 'Template';        $this->emptyValueAsDefault = false;        foreach ((new ModelTemplateCollection())->getCollection() as $template) {            $this->addItem($template->templateName, $template->templateLabel);        }        return parent::getContent();    }}