<?phpnamespace Nemundo\App\ModelDesigner\Com\ListBox;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\App\ModelDesigner\ModelDesignerConfig;use Nemundo\App\ModelDesigner\Parameter\ProjectParameter;use Nemundo\App\ModelDesigner\Project\DefaultProject;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class ProjectListBox extends AdminListBox  // BootstrapListBox{    protected function loadContainer()    {        $this->label = 'Project';        $this->name = (new ProjectParameter())->parameterName;        $this->searchMode = true;        foreach ((new ModelDesignerConfig())->getProjectCollection()->getProjectList() as $project) {            $this->addItem($project->projectName, $project->project . ' (' . $project->namespace . ')');        }        $this->value = (new DefaultProject())->getDefaultProject()->getClassName();        parent::loadContainer();    }    public function getProject()    {        $project = (new ProjectParameter())->getProject();        return $project;    }}