<?phpnamespace Nemundo\App\ModelDesigner\Page;use Nemundo\Admin\Com\Button\AdminIconSiteButton;use Nemundo\Admin\Com\Button\AdminSiteButton;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Table\AdminLabelValueTable;use Nemundo\Admin\Com\Title\AdminSubtitle;use Nemundo\Admin\Com\Title\AdminTitle;use Nemundo\App\ModelDesigner\Com\Breadcrumb\ModelDesignerBreadcrumb;use Nemundo\App\ModelDesigner\Com\Dropdown\TypeDropdown;use Nemundo\App\ModelDesigner\Com\Table\IndexTable;use Nemundo\App\ModelDesigner\Com\Table\ModelTypeTable;use Nemundo\App\ModelDesigner\Parameter\AppParameter;use Nemundo\App\ModelDesigner\Parameter\ModelParameter;use Nemundo\App\ModelDesigner\Parameter\ProjectParameter;use Nemundo\App\ModelDesigner\Site\Data\DataSite;use Nemundo\App\ModelDesigner\Site\Index\IndexNewSite;use Nemundo\App\ModelDesigner\Site\Model\ModelEditSite;use Nemundo\App\ModelDesigner\Template\ModelDesignerTemplate;class ModelPage extends ModelDesignerTemplate{    public function getContent()    {        $project = (new ProjectParameter())->getProject();        $app = (new AppParameter())->getApp($project);        $model = $app->getModelByTableName((new ModelParameter())->getValue());        $layout = new AdminFlexboxLayout($this);        $breadcrumb = new ModelDesignerBreadcrumb($layout);        $breadcrumb->addProject($project);        $breadcrumb->addApp($app);        $breadcrumb->addActiveItem($model->className);        $btn = new AdminIconSiteButton();        $btn->site = ModelEditSite::$site;        $btn->site->addParameter(new ProjectParameter());        $btn->site->addParameter(new AppParameter());        $btn->site->addParameter(new ModelParameter($model->tableName));        $title = new AdminTitle($layout);        $title->content = $model->label . ' ' . $btn->getBodyContent();        $table = new AdminLabelValueTable($layout);        $table->addLabelValue('Template', $model->templateLabel);        $table->addLabelValue('Label', $model->label);        $table->addLabelValue('Class Name', $model->className);        $table->addLabelValue('Table Name', $model->tableName);        $table->addLabelValue('Namespace', $model->namespace);        $table->addLabelValue('Row Class Name', $model->rowClassName);        $table->addLabelValue('Primary Index', $model->primaryIndex->primaryIndexLabel);        $btn = new AdminSiteButton($layout);        $btn->site = DataSite::$site;        $btn->site->addParameter(new ProjectParameter());        $btn->site->addParameter(new AppParameter());        $btn->site->addParameter(new ModelParameter());        $dropdown = new TypeDropdown($layout);        $dropdown->dropdownLabel='New';        $table = new ModelTypeTable($layout);        $table->model = $model;        // Show Deleted        $subtitle = new AdminSubtitle($layout);        $subtitle->content = 'Index';        $btn = new AdminSiteButton($layout);        $btn->site = IndexNewSite::$site;        $btn->site->addParameter(new ProjectParameter());        $btn->site->addParameter(new AppParameter());        $btn->site->addParameter(new ModelParameter());        $table = new IndexTable($layout);        $table->model = $model;        return parent::getContent();    }}