<?phpnamespace Nemundo\App\ModelDesigner\Site;use Nemundo\App\ModelDesigner\Page\AppPage;use Nemundo\App\ModelDesigner\Site\Model\ModelEditSite;use Nemundo\App\ModelDesigner\Site\Model\ModelNewSite;use Nemundo\Web\Site\AbstractSite;class AppSite extends AbstractSite{    /**     * @var AppSite     */    public static $site;    protected function loadSite()    {        $this->url = 'app';        $this->menuActive = false;        AppSite::$site = $this;        new ModelNewSite($this);        new ModelEditSite($this);    }    public function loadContent()    {        (new AppPage())->render();    }}