<?phpnamespace Nemundo\App\ModelDesigner\Site;use Nemundo\App\ClassDesigner\Site\ClassDesignerSite;use Nemundo\App\ModelDesigner\Page\ModelPage;use Nemundo\App\ModelDesigner\Site\Data\DataSite;use Nemundo\App\ModelDesigner\Site\Image\ImageTypeSite;use Nemundo\App\ModelDesigner\Site\Index\IndexDeleteSite;use Nemundo\App\ModelDesigner\Site\Index\IndexNewSite;use Nemundo\App\ModelDesigner\Site\Index\IndexSite;use Nemundo\App\ModelDesigner\Site\Json\ModelTypeJsonSite;use Nemundo\App\ModelDesigner\Site\Model\ModelUndoDeleteSite;use Nemundo\App\ModelDesigner\Site\Type\TypeDeleteSite;use Nemundo\App\ModelDesigner\Site\Type\TypeEditSite;use Nemundo\App\ModelDesigner\Site\Type\TypeNewSite;use Nemundo\App\ModelDesigner\Site\Type\TypeSortableSite;use Nemundo\App\ModelDesigner\Site\Type\TypeUndoDeleteSite;use Nemundo\Web\Site\AbstractSite;class ModelSite extends AbstractSite{    /**     * @var ModelSite     */    public static $site;    protected function loadSite()    {        $this->url = 'model';        $this->menuActive = false;        new ModelUndoDeleteSite($this);        new TypeNewSite($this);        new TypeEditSite($this);        new TypeDeleteSite($this);        new TypeUndoDeleteSite($this);        new IndexSite($this);        new IndexNewSite($this);        new IndexDeleteSite($this);        new ImageTypeSite($this);        new DataSite($this);        new TypeSortableSite($this);        new ModelTypeJsonSite($this);        new ClassDesignerSite($this);        ModelSite::$site = $this;    }    public function loadContent()    {        (new ModelPage())->render();    }}