<?phpnamespace Nemundo\App\ModelDesigner\Site\Project;use Nemundo\App\ModelDesigner\Page\Project\DefaultProjectPage;use Nemundo\Web\Site\AbstractSite;class DefaultProjectSite extends AbstractSite{    /**     * @var DefaultProjectSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Default Project';        $this->url = 'deafult-project';        DefaultProjectSite::$site = $this;    }    public function loadContent()    {        (new DefaultProjectPage())->render();    }}